import React, {useState} from 'react';
import axios from 'axios';


function App() {
  const [data,setData] = useState({})
  const [location,setLocation] = useState({})

  const url  = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=7cbe8fd0a0e569d6669b65ca30c71e97`
  const searchLocation = (event) => {
    if(event.key === 'Enter') { 
    axios.get(url).then((response) => {
      setData(response.data)
      console.log(response.data)
    })
  }
  }

  return (
    <div className="app">
      <div className='search'>
        <input type="text" value={location}  onChange={event => setLocation(event.target.value)}onKeyPress={searchLocation} placeholder='Enter Location' />
      </div>
      <div className='container'>
        <div className='top'>
          <div className='location'> <p>Toronto</p> </div>
          <div className='temp'><h1>60˚F</h1></div>
          <div className='description'><p>Cloudy</p></div>
        </div>
        <div className='bottom'>
          <div className='feels'><p className='bold'>65˚F</p>
          <p>Feels Like</p></div>
          <div className='humidity'> <p className='bold'>20%</p><p>Humidity</p>  </div>
          <div className='wind'><p className='bold'>12 MPH</p> <p>Wind Speed</p></div>
      </div>
    </div>
  </div>
  );
}

export default App;
